package com.demo;
import java.util.*;

public class Quotient_Remaider {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a,b;
		Scanner sc=new Scanner(System.in);
		a=sc.nextDouble();
		b=sc.nextDouble();
		System.out.println("Quotient is "+(a/b)+ " Remainder is " +(a%b));
	}

}
