package com.demo;
import java.util.*;
public class even_odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a;
		a=sc.nextInt();
		if(a%2==0) {
			System.out.println("It is an Even number");
		}
		else {
			System.out.println("It is an Odd number");
		}
	}

}
